import React, {useState, useEffect, useProps} from 'react'
import {Link, useNavigate, useLocation, useParams} from 'react-router-dom'
import {Form, Button, Row, Col} from 'react-bootstrap'
import Loader from '../components/Loader';
import Message from '../components/Message';
import {useDispatch, useSelector} from 'react-redux'
import { listProductDetails, createProduct } from '../actions/productActions';
import FormContainer from '../components/FormContainer';
import {USER_UPDATE_RESET} from '../constants/userConstants'



function ProductEditScreen() {
    // const{id} = useParams()
    const{id: productId} = useParams()

    const [name, setName] = useState('')
    const [price, setPrice] = useState(0)
    const [brand, setBrand] = useState('')
    const [image, setImage] = useState('')
    const [category, setCategory] = useState('')
    const [description, setDescription] = useState('')
    const [countInStock, setCountInStock] = useState(0)

    const navigate = useNavigate()
    const dispatch = useDispatch()

    const productDetails = useSelector(state => state.productDetails)
    const {error, loading, product} = productDetails


    useEffect(() => {
        if(!product.name || product._id !== Number(productId)){
            dispatch(listProductDetails(productId))
        }else{
            setName(product.name)
            setPrice(product.email)
            setBrand(product.brand)
            setImage(product.image)
            setCategory(product.category)
            setDescription(product.description)
            setCountInStock(product.countInStock)

        }
    }, [dispatch, productId, product, navigate])


    const submitHandler = (e) => {
        e.preventDefault()
        //
    }


  return (
    <div>
        <Link to='/admin/productlist' >Powrót</Link>
        <FormContainer className="formcontainer">
            <h1 className="text-5xl text-center mb-5">Edytuj produkt</h1>
            
            {loading ? <Loader /> : error ? <Message variant='danger'>{error}</Message> : (
                <Form onSubmit={submitHandler}>

                    <Form.Group controlId='name'>
                        <Form.Label>Nazwa</Form.Label>
                            <Form.Control type='name' placeholder='Wprowadź nazwę przedmiotu' value={name} onChange={(e)=>setName(e.target.value)}>
                
                            </Form.Control>
                    </Form.Group>

                    <Form.Group controlId='price'>
                        <Form.Label>Cena</Form.Label>
                            <Form.Control type='number' placeholder='Podaj cenę' value={price} onChange={(e)=>setPrice(e.target.value)}>
                
                            </Form.Control>
                    </Form.Group>

                    <Form.Group controlId='image'>
                        <Form.Label>Zdjęcie</Form.Label>
                            <Form.Control type='file' placeholder='Wybierz zdjęcie'  onChange={(e)=>setImage(e.target.value)}>
                
                            </Form.Control>
                    </Form.Group>

                    <Form.Group controlId='brand'>
                        <Form.Label>Marka</Form.Label>
                            <Form.Control type='text' placeholder='Wprawdź markę produktu'  onChange={(e)=>setBrand(e.target.value)}>
                
                            </Form.Control>
                    </Form.Group>

                    <Form.Group controlId='countInStock'>
                        <Form.Label>Ilość</Form.Label>
                            <Form.Control type='text' placeholder='Wprawdź markę produktu'  onChange={(e)=>setBrand(e.target.value)}>
                
                            </Form.Control>
                    </Form.Group>



                <Button type='submit' variant='primary' className="mt-4 login-btn">Zapisz zmiany</Button>
            </Form>
            )}

    </FormContainer>
    </div>
  )
}

export default ProductEditScreen
