import React, {useState, useEffect, useProps} from 'react'
import { useNavigate, useLocation} from 'react-router-dom'
import {Form, Button, Row, Col, LinkContainer, Table} from 'react-bootstrap'
import Loader from '../components/Loader';
import Message from '../components/Message';
import {useDispatch, useSelector} from 'react-redux'
import FormContainer from '../components/FormContainer';
import {listUsers} from '../actions/userActions'



function UserListScreen() {

    const dispatch = useDispatch()

    const userList = useSelector(state => state.userList)
    const {loading, error, users} = userList

    useEffect(() => {
        dispatch(listUsers())
    }, [dispatch])

  return (
    <div>
        <h1 className="text-4xl text-center mb-5">Zarejestrowani użytkownicy</h1>
        {loading
        ? <Loader />
        : error
            ?<Message varint='danger'>{error}</Message>
            :(
                <Table striped bordered hover responsive className="table-sm">
                    <thead>
                        <th>Id</th>
                        <th>Imię</th>
                        <th>Email</th>
                        <th>Administrator</th>
                        <th></th>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user._id}>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>{user.is}</td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            )}
    </div>
  )
}

export default UserListScreen
