import React from 'react';
import { Pagination } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { useLocation } from 'react-router-dom';

function Paginate({ pages, page, keyword = '', isAdmin = false }) {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);

  const keywordParam = searchParams.get('keyword') || '';
  const pageParam = searchParams.get('page') || 1;

  const renderPageLink = (pageNumber) => (
    <LinkContainer
      key={pageNumber}
      to={`/?keyword=${keywordParam}&page=${pageNumber}`}
    >
      <Pagination.Item active={pageNumber === +pageParam}>{pageNumber}</Pagination.Item>
    </LinkContainer>
  );

  return pages > 1 && <Pagination>{Array.from(Array(pages).keys()).map((i) => renderPageLink(i + 1))}</Pagination>;
}

export default Paginate;
