import React, {useState, useEffect, useProps} from 'react'
import {Link, useNavigate, useLocation} from 'react-router-dom'
import {Form, Button, Row, Col, ListGroup} from 'react-bootstrap'
import Loader from '../components/Loader';
import Message from '../components/Message';
import {useDispatch, useSelector} from 'react-redux'
import {register} from '../actions/userActions'
import FormContainer from '../components/FormContainer';
import {saveShippingAddress} from '../actions/cartActions'
import CheckoutSteps from '../components/CheckoutSteps'



function PlaceOrderScreen() {
    const cart = useSelector(state => state.cart)
    

  return (
    <div>
      <CheckoutSteps step1 step2 step3 step4/>
      <Row>
        <Col md={8}>
            <ListGroup cariant='flush'>
                <ListGroup.Item >
                    <h2 className="text-3xl font-bold">Adres dostawy</h2>
                    <p className="pt-4 text-xl">
                        {/* <strong className="text-xl">Podane adres dostawy: </strong> */}
                        {cart.shippingAddress.address}, {cart.shippingAddress.city}
                        {'   '},
                        {cart.shippingAddress.postalCode},
                        {'   '}
                        {cart.shippingAddress.country}
                    </p>
                </ListGroup.Item>

                <ListGroup.Item className="mt-4">
                    <h2 className="text-3xl">Metoda płatności </h2>
                    <p className="pt-4 text-xl">
                        {/* <strong className="text-xl">Wybrana metoda:  </strong> */}
                        {cart.paymentMethod}
                    </p>
                </ListGroup.Item>

            </ListGroup>
        </Col>

        <Col md={4}>
        
        </Col>
      </Row>
    </div>
  )
}

export default PlaceOrderScreen
